import React from 'react';
import '../SignIn/Siginin.css'

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            name: ''
        }
    }

    onNameChange = (event) => {
        this.setState({ name: event.target.value.toLowerCase() })
    }

    onEmailChange = (event) => {
        this.setState({ email: event.target.value.toLowerCase() })
    }

    onPasswordChange = (event) => {
        this.setState({ password: event.target.value })
    }

    onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin', {
            method: 'post',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
            .then(response => response.json())
            .then(data => {
                if (data === 'Success') {
                    this.props.onRouteChange('home')
                }
            })
    }

    render() {
        return (
            <div id='signin-form' className="bg-grey-lighter flex flex-col sm:w-full h-auto m-auto">
                <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2">
                    <div className="px-6 py-8 rounded shadow-xl text-black w-full">
                        <h1 className="text-white mb-8 text-3xl text-center">Create Account</h1>

                        <input
                            type="text"
                            className="block border border-grey-light w-full p-3 rounded mb-4"
                            name="user-id"
                            placeholder="Name"
                            onNameChange={this.on} />

                        <input
                            type="text"
                            className="block border border-grey-light w-full p-3 rounded mb-4"
                            name="email"
                            placeholder="Email" />

                        <input
                            type="password"
                            className="block border border-grey-light w-full p-3 rounded mb-4"
                            name="password"
                            placeholder="Password" />

                        <button
                            onClick={() => onRouteChange('home')}
                            id='submit-form'
                            type="submit"
                            className="white w-full text-center py-3 rounded bg-blue-600 text-gray-800 hover:text-white focus:text-white hover:bg-green-dark focus:outline-none my-1"
                        >Sign Up
                        </button>
                        <p className='development'>ðŸ”§ Not implemented yet, you can pass. ðŸ”§</p>
                    </div>

                    <div className="text-grey-dark mt-6 bg-white px-2 py-5 rounded-lg shadow-md text-black w-full">
                        Already have an account?
                        <button
                            className="text-lg no-underline border-b text-blue-700 transition duration-200 hover:border-blue-700 text-blue ml-2"
                            href="../login/"
                            onClick={() => onRouteChange('signin')}>
                            Log in.
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Register;
