import React from "react"
import "./Siginin.css"
import LoadingScreen from "react-loading-screen"

class SignIn extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      signInEmail: "",
      signInPassword: "",
      loading: false,
    }
  }

  onEmailChange = (event) => {
    this.setState({ signInEmail: event.target.value.toLowerCase() })
  }

  onPasswordChange = (event) => {
    this.setState({ signInPassword: event.target.value })
  }

  onSubmitSignIn = () => {
    this.setState({ loading: true })
    fetch("https://smart-brain-api-z87p.onrender.com/signin", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        email: this.state.signInEmail,
        password: this.state.signInPassword,
      }),
    })
      .then((response) => response.json())
      .then((user) => {
        if (user.id) {
          this.props.loadUser(user)
          this.props.onRouteChange("home")
        }
        this.setState({ loading: false })
      })
  }

  onFormSubmit = (event) => {
    event.preventDefault()
    this.onSubmitSignIn()
  }

  onDemoAccountLogin = () => {
    const demoEmail = "guest@gmail.com"
    const demoPassword = "123"

    this.setState({
      signInEmail: demoEmail,
      signInPassword: demoPassword,
      loading: true,
    })

    fetch("https://smart-brain-api-z87p.onrender.com/signin", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        email: demoEmail,
        password: demoPassword,
      }),
    })
      .then((response) => response.json())
      .then((user) => {
        if (user.id) {
          this.props.loadUser(user)
          this.props.onRouteChange("home")
        }
        this.setState({ loading: false })
      })
  }

  render() {
    const { onRouteChange } = this.props

    return (
      <LoadingScreen
        loading={this.state.loading}
        bgColor="transparent"
        spinnerColor="#ffffff"
        textColor="#ffffff"
        logoSrc=""
        text="Loading your content..."
      >
        <form
          onSubmit={this.onFormSubmit}
          id="signin-form"
          className="bg-grey-lighter flex flex-col sm:w-full h-auto m-auto"
        >
          <div className="container max-w-sm mx-auto flex-1 flex flex-col items-center justify-center px-2">
            <div className="px-6 py-8 rounded shadow-xl text-black w-full">
              <h1 className="text-white mb-0 text-3xl text-center">
                {" "}
                - Face Scan Pro -{" "}
              </h1>
              <h1 className="text-white mb-8 mt-0 text-2xl text-center">
                Face-Recognition App
              </h1>
              <input
                onChange={this.onEmailChange}
                type="text"
                className="block border border-grey-light w-full p-3 rounded mb-4"
                name="email"
                placeholder="Email"
              />

              <input
                onChange={this.onPasswordChange}
                type="password"
                className="block border border-grey-light w-full p-3 rounded mb-4"
                name="password"
                placeholder="Password"
              />

              <button
                id="submit-form"
                type="submit"
                className="white w-full text-center py-3 rounded bg-blue-600 text-gray-800 hover:text-white focus:text-white focus:outline-none my-3"
              >
                Log in
              </button>

              <p className="development">- OR -</p>

              <button
                type="button"
                className="white w-full text-center py-3 rounded bg-gradient-to-r from-blue-600/80 to-sky-300/80 hover: bg-gradient-to-r from-blue-600/80 to-sky-300/80 text-gray-800 hover:text-white focus:outline-none my-3 ease-in duration-150"
                onClick={this.onDemoAccountLogin}
              >
                Demo Account Log in
              </button>
            </div>

            <div className="text-grey-dark mt-6 bg-white px-4 py-5 rounded-lg shadow-md text-black w-full">
              Don't have an account?
              <button
                className="text-lg no-underline border-b text-blue-700 transition duration-200 hover:border-blue-700 text-blue ml-2 ease-in"
                href="../login/"
                onClick={() => onRouteChange("register")}
              >
                Create one!
              </button>
            </div>
          </div>
        </form>
      </LoadingScreen>
    )
  }
}

export default SignIn
